INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-welcome-note', 'info', 'en_US', 'New feature(s)', 'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-24 20:50:54', NULL, 0),
(2, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Looking for the Store Notice setting?', 'Τώρα μπορείτε να το βρείτε στον Προσαρμογέα.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-24 20:50:54', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-24 20:50:54', NULL, 0),
(4, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Καλώς ήρθατε στο WooCommerce! Ρυθμίστε το κατάστημά σας και ξεκινήστε τις πωλήσεις', 'Είμαστε εδώ για να σας βοηθήσουμε να εφαρμόσετε τα πιο σημαντικά βήματα για να λειτουργήσει το κατάστημά σας.', 'info', '{}', 'actioned', 'woocommerce-admin', '2020-05-26 07:20:29', NULL, 0),
(5, 'wc-admin-task-list-complete', 'info', 'en_US', 'Congratulations - your store is ready for launch!', 'You’re ready to take your first order - may the sales roll in!', 'notice', '{}', 'unactioned', 'woocommerce-admin', '2020-05-26 13:26:57', NULL, 0),
(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Εγκατάσταση εφαρμογής Woo mobile', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-27 16:26:43', NULL, 0),
(7, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Συμβουλές, ενημερώσεις προϊόντων και έμπνευση', 'Είμαστε εδώ για εσάς — λάβετε συμβουλές. ενημερώσεις προϊόντων και έμπνευση κατευθείαν στην αλληλογραφία σας.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-05-27 16:26:43', NULL, 0),
(8, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Βαθμολογήστε την εμπειρία σας', 'Αν σας αρέσει το WooCommerce Admin παρακαλούμε αφήστε μας μία βαθμολογία 5 αστέρων. Ευχαριστούμε εκ των προτέρων!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-28 19:03:05', NULL, 0),
(9, 'wc-admin-facebook-extension', 'info', 'en_US', 'Πώληση στο Facebook', 'Αναπτύξτε την επιχείρησή σας στοχεύοντας τα σωστά άτομα και αυξάνοντας τις πωλήσεις μέσω του Facebook. Μπορείτε να εγκαταστήσετε αυτήν τη δωρεάν επέκταση τώρα.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2020-05-28 19:03:05', NULL, 0),
